/*TC、MAC、RLC模块需要用到该枚举定义*/
enum TEST_LOOP_TYPE_ENUM
{
      TEST_LOOP_NULL                    =0,                 /* 测试环回模式未激活 */
      TEST_LOOP_MODE1                   =1,                 /* 测试环回模式1*/
      TEST_LOOP_MODE2                   =2,                 /* 测试环回模式2*/
      TEST_LOOP_BUTT                                       /* 用这个统一的BUTT，作为最后一个枚举值。*/
} ;
typedef VOS_UINT8 TEST_LOOP_TYPE_ENUM_UINT8;

